var cartItems=document.querySelectorAll(".cart-link"),cartPopup=document.querySelector(".modal-cart"),cartClose=cartPopup.querySelector(".modal-close");for(index=0;index<cartItems.length;index++)cartItems[index].addEventListener("click",function(e){e.preventDefault(),cartPopup.classList.add("modal-show")});cartClose.addEventListener("click",function(e){e.preventDefault(),cartPopup.classList.remove("modal-show")}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),cartPopup.classList.contains("modal-show")&&cartPopup.classList.remove("modal-show"))});